shader.vertex:
attribute vec3 Position;
attribute vec4 Color;
attribute vec2 TexCoord;
attribute vec4 RenderData;
attribute float Scale;
attribute float Time;
varying vec4 Color0;
varying vec2 TexCoord0;
varying vec4 RenderDataOut;
varying float ScaleOut;
varying float TimeOut;
uniform mat4 Transform;
void main(void)
{
    RenderDataOut = RenderData;
    ScaleOut = Scale;           // Passing data to fragment shader
    TimeOut = Time;             // Passing data to fragment shader
    Color0 = Color;
    TexCoord0 = TexCoord;
    gl_Position = Transform * vec4(Position.xyz, 1.0);
}

shader.fragment:
varying lowp vec4 Color0;
varying mediump vec2 TexCoord0;
varying lowp vec4 RenderDataOut;
varying lowp float ScaleOut;
varying lowp float TimeOut;
uniform sampler2D Texture0;
void main(void)
{
    vec4 Color = Color0 * texture2D(Texture0, TexCoord0);
    Color.r += TimeOut * 0.2;
    gl_FragColor = Color;
}

shader.vertex:
attribute vec3 Position; 
attribute vec4 Color; 
attribute vec2 TexCoord; 
attribute vec4 RenderData; 
attribute float Scale;
attribute vec2 PlayerPos;
attribute float Time;

varying vec4 Color0; 
varying vec2 TexCoord0; 
varying vec4 RenderDataOut; 
varying float ScaleOut;
varying vec2 PlayerPosOut;
varying float TimeOut;
varying float Distance;
uniform mat4 Transform; 

void main(void) 
{
    RenderDataOut = RenderData;
    ScaleOut = Scale;           // Passing data to fragment shader
    TimeOut = Time;             // Passing data to fragment shader
    Color0 = Color;


    vec2 coord = TexCoord;


    vec2 pos = (PlayerPos / RenderData.zw) * Scale;

    float dist = distance(TexCoord, pos);

    if (dist < 0.1 & false) {

        float _Strength = 0.01;
        float _Frequency = 100; 
        float _Speed = 0.25;

        float sineNoise = sin(_Frequency * TexCoord.y - Time * _Speed);
        float offset = sineNoise * _Strength; 


        float t;
        float edge0 = 1 - TexCoord.y;
        float edge1 = 0.45;
        float x = 0.6;
        t = clamp((x - edge0) / (edge1 - edge0), 0.0, 1.0);
        offset *= t * t * (3.0 - 2.0 * t);

        coord.x += offset;
    };

    TexCoord0 = coord;
    gl_Position = Transform * vec4(Position.xyz, 1.0);

    Distance = dist;
}

shader.fragment:
varying lowp vec4 Color0; 
varying mediump vec2 TexCoord0; 
varying lowp vec4 RenderDataOut;
varying lowp float ScaleOut; 
varying mediump vec2 PlayerPosOut;
varying lowp float TimeOut; 
varying lowp float Distance; 
uniform sampler2D Texture0; 
void main(void)
{
    vec4 Color = Color0 * texture2D(Texture0, TexCoord0);
    if (Distance < 0.52675) {
        Color.r += TimeOut * 0.2;
    }
    gl_FragColor = Color;
}

shader.vertex:
attribute vec3 Position; 
attribute vec4 Color; 
attribute vec2 TexCoord; 
attribute vec4 RenderData; 
attribute float Scale;
attribute vec2 PlayerPos;
attribute vec2 CenterPos;
attribute float Time;
attribute float VenusTime;

varying vec4 Color0; 
varying vec2 TexCoord0; 
varying vec4 RenderDataOut; 
varying float ScaleOut;
varying vec2 PlayerPosOut;
varying vec2 CenterPosOut;
varying float TimeOut;
varying float VenusTimeOut;
uniform mat4 Transform; 

void main(void) 
{
    RenderDataOut = RenderData;
    ScaleOut = Scale;           // Passing data to fragment shader
    PlayerPosOut = PlayerPos;
    CenterPosOut = CenterPos;
    TimeOut = Time;             // Passing data to fragment shader
    VenusTimeOut = VenusTime;             // Passing data to fragment shader
    Color0 = Color;


    vec2 pos = (PlayerPos / RenderData.zw) * Scale;


    vec2 diff = TexCoord - pos;
    float dist = diff.x*diff.x + diff.y*diff.y;


    vec2 coord = TexCoord;

    float margin = 0.5;
    if ( dist < margin ) {
        float _Strength = 0.005 * (VenusTimeOut / 60);
        float _Frequency = 100; 
        float _Speed = 0.25;

        float sineNoise = sin(_Frequency * TexCoord.y - Time * _Speed);
        float offset = sineNoise * _Strength; 

        //float t;
        //float edge0 = 1 - TexCoord.y;
        //float edge1 = 0.45;
        //float x = 0.6;
        //t = clamp((x - edge0) / (edge1 - edge0), 0.0, 1.0);
        //offset *= t * t * (3.0 - 2.0 * t);

        coord.x += offset;
    }

    TexCoord0 = coord;
    gl_Position = Transform * vec4(Position.xyz, 1.0);
}

shader.fragment:
varying lowp vec4 Color0; 
varying mediump vec2 TexCoord0; 
varying lowp vec4 RenderDataOut;
varying lowp float ScaleOut; 
varying mediump vec2 PlayerPosOut;
varying mediump vec2 CenterPosOut;
varying lowp float TimeOut; 
varying lowp float VenusTimeOut; 
uniform sampler2D Texture0; 
void main(void)
{
    vec4 Color = Color0 * texture2D(Texture0, TexCoord0);

    vec2 pos = (PlayerPosOut / RenderDataOut.zw) * ScaleOut;

    float dist = distance(TexCoord0, pos );
    if (dist < 10) {
        Color.r += (0.2 / dist/2) * (VenusTimeOut / 60);
        Color.g += (0.1 / dist/5) * (VenusTimeOut / 60);
    }
    gl_FragColor = Color;
}
